var posts_no = 10;
var showpoststhumbs = true;
var posts_date = true;
var post_summary = false;
var summary_chars = 50;

function autoslide(t) {
    document.write('<ul class="jh-slider-posts-images">');
    for (var e = 0; e < posts_no; e++) {
        var r, n = t.feed.entry[e],
            i = n.title.$t;
        if (e == t.feed.entry.length) break;
        for (var o = 0; o < n.link.length; o++) {
            if ("replies" == n.link[o].rel && "text/html" == n.link[o].type) var l = n.link[o].title,
                m = n.link[o].href;
            if ("alternate" == n.link[o].rel) {
                r = n.link[o].href;
                break
            }
        }
        var u;
        try {
            u = n.media$thumbnail.url.replace(/\/s[0-9]+\-c/g, "/s" + h.ImageSize + "-c")
        } catch (h) {
            s = n.content.$t, a = s.indexOf("<img"), b = s.indexOf('src="', a), c = s.indexOf('"', b + 5), d = s.substr(b + 5, c - b - 5), u = -1 != a && -1 != b && -1 != c && "" != d ? d : "https://lh3.googleusercontent.com/Uys1Am0umSrYK1ayz0k9wLMSQvUmKtZ2jFuhNHG_2C1yKviEIJ7d5zTKp_hiB9iejsAkwHloVdF_hRuo-J0J3r3f5wee-LDeqVo6LH96ZgI8d1Mlj7N-nF6sqaBNpev-cKwXLMt08nOyL3FCkUX4nR-kaM_TtYxA1DXJfPHx6OKrCrsUz4AwO33qMHhUTFq4YSqbiELTNWXteKzDLsSxDEJuj4_NOhpGKtrMUwgVKlKFzbJ3DTTvArxP1Lfxl0STHZhDE6IakO_US8iKk-i0ON7ScHmu2_TMI038P-QUJlV-BuL1q-O3mJKKB1xPLCymaph800sin5W7FE9ejt3TQdkZu2Hzbm-upp5uvYgcpCg8vrVKeg8i2hx4aBbclgsR9I5gULoNDLUOzKf7ZcQ1QdQAOexxaYX8wRt16CkP0erGkbmqv4qh6Ga-lOCf5leYTvmzrLAM40rrlzQk25JxhiuA55HpfXkEU0FTXP2XAp3VE_eMOCr3kFXtK0udZQKj09N6qc0OD-VKR76RraN0byy_DiJZEmQa57cxWk77jG4c08vA6mCAtziNXphfKsJmDE8QvK1J2LOcBi--HgubQ41Xh-moO9d2OpFzCOK-=s985-no"
        }
        var p = n.published.$t,
            f = p.substring(0, 4),
