            v = p.substring(8, 10),
            w = new Array;
        if (w[1] = "January", w[2] = "February", w[3] = "March", w[4] = "April", w[5] = "May", w[6] = "June", w[7] = "July", w[8] = "August", w[9] = "September", w[10] = "October", w[11] = "November", w[12] = "December", document.write('<div class="slide-post">'), 1 == showpoststhumbs && document.write('<div class="jh-slider-thumbnails" style="background:url(' + u + ')"></div>'), document.write('<a href="' + r + '"><div class="slider-one"><div class="slider-two"><div class="slider-three"><div class="slider-four">'), document.write('<div class="slider-titles-font">' + i + "</div>"), document.write('<div class="slider-date">' + w[parseInt(g, 10)] + " " + v + ", " + f + "</div>"), "content" in n) var A = n.content.$t;
        else if ("summary" in n) var A = n.summary.$t;
        else var A = "";
        var k = /<\S[^>]*>/g;
        if (A = A.replace(k, ""), 1 == post_summary)
            if (A.length < summary_chars) document.write(A);
            else {
                document.write('<div class="slide-snippet">');
                A = A.substring(0, summary_chars);
                var y = A.lastIndexOf("");
