head.script:
//
var relatedTitles = new Array();
var relatedTitlesNum = 0;
var relatedUrls = new Array();
var thumburl = new Array();

function related_results_labels_thumbs(e) {
    for (var i = 0; i < e.feed.entry.length; i++) {
        var f = e.feed.entry[i];
        relatedTitles[relatedTitlesNum] = f.title.$t;
        try {
            thumburl[relatedTitlesNum] = f.gform_foot.url
        } catch (error) {
            s = f.content.$t;
            a = s.indexOf("<img");
            b = s.indexOf("src=\"", a);
            c = s.indexOf("\"", b + 5);
            d = s.substr(b + 5, c - b - 5);
            if ((a != -1) && (b != -1) && (c != -1) && (d != "")) {
                thumburl[relatedTitlesNum] = d
            } else thumburl[relatedTitlesNum] = 'https://lh3.googleusercontent.com/Uys1Am0umSrYK1ayz0k9wLMSQvUmKtZ2jFuhNHG_2C1yKviEIJ7d5zTKp_hiB9iejsAkwHloVdF_hRuo-J0J3r3f5wee-LDeqVo6LH96ZgI8d1Mlj7N-nF6sqaBNpev-cKwXLMt08nOyL3FCkUX4nR-kaM_TtYxA1DXJfPHx6OKrCrsUz4AwO33qMHhUTFq4YSqbiELTNWXteKzDLsSxDEJuj4_NOhpGKtrMUwgVKlKFzbJ3DTTvArxP1Lfxl0STHZhDE6IakO_US8iKk-i0ON7ScHmu2_TMI038P-QUJlV-BuL1q-O3mJKKB1xPLCymaph800sin5W7FE9ejt3TQdkZu2Hzbm-upp5uvYgcpCg8vrVKeg8i2hx4aBbclgsR9I5gULoNDLUOzKf7ZcQ1QdQAOexxaYX8wRt16CkP0erGkbmqv4qh6Ga-lOCf5leYTvmzrLAM40rrlzQk25JxhiuA55HpfXkEU0FTXP2XAp3VE_eMOCr3kFXtK0udZQKj09N6qc0OD-VKR76RraN0byy_DiJZEmQa57cxWk77jG4c08vA6mCAtziNXphfKsJmDE8QvK1J2LOcBi--HgubQ41Xh-moO9d2OpFzCOK-=s985-no'
        }
        if (relatedTitles[relatedTitlesNum].length > 200) relatedTitles[relatedTitlesNum] = relatedTitles[relatedTitlesNum].substring(0, 200) + "...";
        for (var k = 0; k < f.link.length; k++) {
            if (f.link[k].rel == 'alternate') {
                relatedUrls[relatedTitlesNum] = f.link[k].href;
                relatedTitlesNum++
            }
        }
    }
}

function removeRelatedDuplicates_thumbs() {
    var a = new Array(0);
    var b = new Array(0);
    var c = new Array(0);
    for (var i = 0; i < relatedUrls.length; i++) {
        if (!contains_thumbs(a, relatedUrls[i])) {
            a.length += 1;
            a[a.length - 1] = relatedUrls[i];
            b.length += 1;
            c.length += 1;
            b[b.length - 1] = relatedTitles[i];
            c[c.length - 1] = thumburl[i]
        }
    }
    relatedTitles = b;
    relatedUrls = a;
    thumburl = c
}

function contains_thumbs(a, e) {
    for (var j = 0; j < a.length; j++)
        if (a[j] == e) return true;
    return false
}

function printRelatedLabels_thumbs() {
    for (var i = 0; i < relatedUrls.length; i++) {
        if ((relatedUrls[i] == currentposturl) || (!(relatedTitles[i]))) {
            relatedUrls.splice(i, 1);
            relatedTitles.splice(i, 1);
            thumburl.splice(i, 1);
            i--
        }
    }
    var r = Math.floor((relatedTitles.length - 1) * Math.random());
    var i = 0;
    if (relatedTitles.length > 0) document.write('<h2>' + relatedpoststitle + '</h2>');
    document.write('');
    while (i < relatedTitles.length && i < 20 && i < maxresults) {
        document.write('<div class="jh-related-s"><div class="jh-r-section-space"><a ');
        if (i != 0) document.write('"');
        else document.write('"');
        document.write(' href="' + relatedUrls[r] + '"><div class="jh-related-post-thumbnails" style="background: url(' + thumburl[r] + ')"></div><div class="jh-r-img-1"><div class="jh-r-img-2"><div class="jh-r-img-3"><div class="jh-r-img-4"><div class="jh-r-titles">' + relatedTitles[r] + '</div></div></div></div></div></div></a></div>');
        if (r < relatedTitles.length - 1) {
            r++
        } else {
            r = 0
        }
        i++
    }
    document.write('');
    relatedUrls.splice(0, relatedUrls.length);
    thumburl.splice(0, thumburl.length);
    relatedTitles.splice(0, relatedTitles.length)
}//

