    }

    function f() {
        if (_WidgetManager._GetAllData().blog.pageType == "item") {
            return
        }
        a = c("a.blog-pager-older-link").attr("href");
        if (!a) {
            return
        }
        var i = c('<a class="jh-load" href="javascript:;">Load More Posts</a>');
        i.click(d);
        var h = c('<div class="jh-load" style="display: none;">Please wait...</div>');
        b = c('<div class="jh-loading-more"></div>');
        b.append(i);
        b.append(h);
        b.insertBefore(c("#blog-pager"));
        c("#blog-pager").hide()
    }
    e.ready(f)
})(jQuery);
//
